function monthafter(){
    if ($("#october").css("display") != "none") {
        $("#october").fadeOut(400, function() {
            $("#november").css("display", "block").fadeIn(400);
        });
        $("#month").fadeOut(400, function() {
            $(this).text("November").fadeIn(400);
        });
        $("#calendarmonth").fadeOut(400, function() {
            $(this).text("November 2022").fadeIn(400);
        });
        $("#datecolumn").animate({
            height: '550px',
        });
        $("#calendarcolumn").animate({
            height: '550px',
        });
        // $("#october").css("display", "none");
        // $("#november").css("display", "block");
    }
    else if ($("#september").css("display") != "none") {
        $("#september").fadeOut(400, function() {
            $("#october").css("display", "block").fadeIn(400);
        });
        $("#month").fadeOut(400, function() {
            $(this).text("October").fadeIn(400);
        });
        $("#calendarmonth").fadeOut(400, function() {
            $(this).text("October 2022").fadeIn(400);
        });
        $("#datecolumn").animate({
            height: '603px',
        });
        $("#calendarcolumn").animate({
            height: '603px',
        });
    }
    // } else if ($("#txt-2").css("display") != "none") {
    //     $("#txt-1").css("display", "none");
    //     $("#txt-2").css("display", "none");
    //     $("#txt-3").css("display", "block");
    // } else {
    //     $("#txt-1").css("display", "block");
    //     $("#txt-2").css("display", "none");
    //     $("#txt-3").css("display", "none");
    // }
};

function monthbefore(){
    if ($("#october").css("display") != "none") {
        $("#october").fadeOut(400, function() {
            $("#september").css("display", "block").fadeIn(400);
        });
        $("#month").fadeOut(400, function() {
            $(this).text("September").fadeIn(400);
        });
        $("#calendarmonth").fadeOut(400, function() {
            $(this).text("September 2022").fadeIn(400);
        });
        $("#datecolumn").animate({
            height: '550px',
        });
        $("#calendarcolumn").animate({
            height: '550px',
        });
    }
    else if ($("#november").css("display") != "none") {
        $("#november").fadeOut(400, function() {
            $("#october").css("display", "block").fadeIn(400);
        });
        $("#month").fadeOut(400, function() {
            $(this).text("October").fadeIn(400);
        });
        $("#calendarmonth").fadeOut(400, function() {
            $(this).text("October 2022").fadeIn(400);
        });
        $("#datecolumn").animate({
            height: '603px',
        });
        $("#calendarcolumn").animate({
            height: '603px',
        });
    }
};
